drop schema if exists person;
drop schema if exists stad;
drop schema if exists user;

create schema person(
    id int primary key auto_increment,
    filename varchar(50) default "default.jpg",
    name varchar(255),
    content varchar(10000) default "",
    type varchar(50),
    rating int default 0,
    cob int default 0
);

create schema stad(
    id int primary key auto_increment,
    filename varchar(50) default "default.jpg",
    name varchar(50),
    content varchar(10000) default "",
    rating int default 0
);

create schema user(
    usr_id int primary key auto_increment,
    usr_voornaam varchar(50),
    usr_naam varchar(50),
    usr_email varchar(255) unique,
    usr_password varchar(255),
    usr_avatar varchar(255) default "default.jpg",
    usr_type varchar(5) default "user"
);

